import './App.css';
import LandingPage from "./pages/landingPage"

//Import Charkra UI(Tiffany)
import { ChakraProvider } from '@chakra-ui/react'

function App() {
  return (
  //Wrap application in chakra provider at root(TIffany)
  <ChakraProvider>

<ApolloProvider client={client}>
      <Router>
        <div>
          <StoreProvider>
            <Nav />
            <Routes>
              <Route 
                path="/" 
                element={<Home />} 
              />
              <Route 
                path="/login" 
                element={<Login />} 
              />
              <Route 
                path="/signup" 
                element={<Signup />} 
              />
              <Route 
                path="/success" 
                element={<Success />} 
              />
              <Route 
                path="/orderHistory" 
                element={<OrderHistory />} 
              />
              <Route 
                path="/products/:id" 
                element={<Detail />} 
              />
              <Route 
                path="*" 
                element={<NoMatch />} 
              />
            </Routes>
          </StoreProvider>
        </div>
      </Router>
    </ApolloProvider>
  </ChakraProvider>
  );
}

export default App;
